import axios from 'axios';

const API = axios.create({ baseURL:'http://localhost'})
const url = 'http://localhost:5000/posts';
const tokenUrl = 'http://localhost:5000/auth/google';
const userUrl = 'http://localhost:5000/user'
export const fetchPosts = () => axios.get(url);
export const createPost = (newPost) => axios.post(url, newPost);
export const updatePost = (currentId, post) => axios.patch(`${url}/${currentId}`, post);
export const deletePost = (currentId) => axios.delete(`${url}/${currentId}`);

export const getToken = (code) => axios.post(tokenUrl,code);
export const signUp = (data) => axios.post(`${userUrl}/signup`, data);
export const signIn = (data) => axios.post(`${userUrl}/signin`, data);
