import { useRecoilValue } from 'recoil'
import {posts as p } from './posts.js'
const posts = useRecoilValue(p);
//update state using hooks
export const requests =  (action) => {
    switch(action) {
        case 'FETCH_ALL':
            return posts
        case 'CREATE':
            //have a function that creates a post and adds it to the shit
            return posts
        default:
            return posts
    }

}
