import { useRecoilValue, useSetRecoilState } from 'recoil'
import {posts} from './posts.js'
//update state using hooks

export const Requests = (action) => {
    
    switch(action) {
        case 'FETCH_ALL':
            console.log(posts);
        case 'CREATE':
            //have a function that creates a post and adds it to the shit
            return posts
        default:
            return posts
    }

}
