import {React, useEffect, useState} from "react";
import {TextField, Button, Typography, Paper} from '@material-ui/core';
import useStyles from './styles.js';
import FileBase from 'react-file-base64';
import * as api from '../../api';
import { useSetRecoilState, useRecoilState } from "recoil";
import { posts as p, currentId as curId, currentId } from "../../atoms/posts.js";



const Form = () => {
    const classes = useStyles();
    const [postData,setPostData] = useState({
        title:'', message:'', tags:'', selectedFile:''
    });
    
    // const setPosts = useSetRecoilState(p);
    const [posts, setPosts] = useRecoilState(p);
    const [currentId, setId] = useRecoilState(curId); 
    const Post = (currentId ? posts.find((p)=> p._id === currentId) :  null);
    
    //2 things
    //figure out how to refresh properlu
    //y cant just updating the state causew a post update: I KNOW WHY THE MAP SHIT ISNT UPDATING NORMALLY, ITS ONLY GRABBING SHIT FROM THE DB
    const GetPosts = async() => {
    
        try {
             var {data} = await api.fetchPosts();
            setPosts(data);
        } catch (error) {
            console.log(error);
        }
       
    }

    const createPost = async(POSTS) => {
        try {
            const {data}  = await api.createPost({...POSTS});
            //if data doesnt replace data with post on the line with setPost
            setPosts((posts) =>[...posts,data]);
            console.log(posts.length);
        } catch (error) {
            console.log(error);
        }
    }
    const updatePost = async(Id, post) => {
        try {
            const {data} = await api.updatePost(Id, post);
            console.log(typeof posts);
            const pos = posts.map((Post) => Post._id === data._id ? data : Post);
            setPosts(pos); 
        } catch (error) {
            console.log(error);
        }
    };
    const handleChange = (e) => {
        setPostData({...postData, [e.target.name]: e.target.value});

    };
    const handleSubmit = (e) => {
            e.preventDefault();
            if(currentId === '') {
                createPost(postData);
            } else {
              updatePost(currentId, postData)
            }//submit and then fprm data is immediatley reverted
            //need to subscribe posts component to recoil so it gets updated asap
            clear();
    }

    useEffect(()=> {
        if(Post) {setPostData(Post)
        };
    },[Post]);

    const clear = () => {
        setId('');
        setPostData({
            title:'', message:'', tags:'', selectedFile:'',
        })
    }

    return(
           <Paper className={classes.paper}>
               <form autoComplete="off" noValidate className={`${classes.root} ${classes.form}`} onSubmit={handleSubmit}>
                   <Typography variant="h6">{currentId ? 'Editing' : 'Creating'} a Event/Memory</Typography>
                   <TextField name="title" variant="outlined" label="Title" fullWidth value={postData.title} onChange={(e)=>setPostData({...postData,title:e.target.value})}/>
                   <TextField name="message" variant="outlined" label="Message" fullWidth value={postData.message} onChange={(e)=>setPostData({...postData,message:e.target.value})}/>
                   <TextField name="tags" variant="outlined" label="Tags" fullWidth value={postData.tags} onChange={(e)=>setPostData({...postData,tags:e.target.value.split(',')})}/>
                    <div className={classes.fileInput}>
                        <FileBase type="file" multiple={false} onDone={({base64}) => setPostData({...postData, selectedFile:base64})}/>
                        <Button className={classes.buttonSubmit} variant="contained" color="primary" size="large" type="submit" fullWidth>Submit</Button>
                        <Button variant="contained" color="secondary" size="small" onClick={clear}fullWidth>Clear</Button>

                    </div>
               </form>
           </Paper> 
        )
}
export default Form;
