import {React, useEffect} from "react";
import {Container, AppBar, Typography, Grow, Grid} from '@material-ui/core';
import Form from "./components/Forms/Form.js";
import Posts from './components/Posts/Posts.js';
import useStyles from './styles.js';
import {GetPosts} from './actions/posts.js';
import { fetchPosts } from "./api/index.js";
import { Requests } from "./atoms/index.js";
import {  posts as p } from "./atoms/posts.js";
import { useRecoilValue, useSetRecoilState } from "recoil";
import * as api from './api';


const App = () => {
    const classes = useStyles();
    
    const setPosts = useSetRecoilState(p);
    useEffect(() => {
         const GetPosts = async() => {
    
            try {
                 var {data} = await api.fetchPosts();
                setPosts(data);
            } catch (error) {
                console.log(error);
            }
           
        }
        // const data = GetPosts();
        GetPosts();
        // const data =  GetPosts();
        // setPosts(data);

    }, []);
   
    return(
       
    
        <Container maxWidth='lg'>
            <AppBar className={classes.appBar} position="static" color="inherit">
                <Typography className = {classes.heading} variant="h2" align="center">Memories/Events</Typography>
            </AppBar>
            <Grow in>
                <Grid container justifyContent ="space between" alignItems="stretch" spacing={3}>
                    <Grid item xs = {12} sm ={7}>
                        <Posts/>
                    </Grid>
                    <Grid item xs = {12} sm ={4}>
                        <Form/>
                    </Grid>
                </Grid>

            </Grow>
        </Container>
    )
}
export default App;
