import {React, useState, useEffect} from 'react';
import { AppBar, Avatar, Button, Toolbar, Typography } from '@material-ui/core';
import useStyles from './styles.js';
import { Link } from 'react-router-dom';
import {useNavigate} from 'react-router-dom';
import { users} from '../../atoms/users.js';
import { useRecoilState } from 'recoil';


const Navbar = () => {
    const navigate = useNavigate();

    const classes = useStyles();
    const [user,setUser] = useRecoilState(users);
    const logout = () => {

        navigate('/')

        setUser(null);
    }
    // useEffect(()=> {
    //     const token = user?.access;

    //     setUser(JSON.parse(localStorage.getItem('profile')));
    // },[]);
    return (
        <AppBar className={classes.appBar} position="static" color="inherit">
            <div className={classes.brandContainer}>
                <Typography component={Link} to='/' className = {classes.heading} variant="h2" align="center">Memories/Events</Typography>
                
            </div>
            <Toolbar className={classes.toolbar}>
                {user ? (
                    <div className={classes.profile}>
                        <Avatar className={classes.purple} alt = {user.User.name} src ={user.User.picture}>
                            {user.User.name.charAt(0)}
                        </Avatar>
                        <Typography className={classes.userName} variant = 'h6'>{user.User.name}</Typography>
                        <Button variant = "contained" className={classes.logout} color = "secondary" onClick={logout}>Logout</Button>
                        

                    </div>
                ) : (
                    <Button component = {Link} to= "/auth" variant = "contained" color = "primary" >Sign In</Button>    
                )
            }
            </Toolbar>
            </AppBar>
    )
}
export default Navbar
