import { useRecoilBridgeAcrossReactRoots_UNSTABLE, useRecoilValue } from 'recoil';
import * as api from '../api';
import { Requests } from '../atoms';
import { posts as p } from '../atoms/posts';

export const getPosts = async() => {
    const[posts,setPosts] = useRecoilValue(p);
    try {
        const {data} = await api.fetchPosts();
        setPosts(data);
        //Requests('FETCH_ALL');
    } catch (error) {
        console.log(error);
    }
    
}
