import { useRecoilValue, useSetRecoilState } from 'recoil'
import {posts as p} from './posts.js'
//update state using hooks

export const Requests = (action,data) => {  
    const[posts,setPosts] = useRecoilValue(p);
    switch(action) {
        case 'FETCH_ALL':
            setPosts(data)
            return posts
        case 'CREATE':
            //have a function that creates a post and adds it to the shit
            return posts
        default:
            return posts
    }
    

}
